[y_predVar, y_predProMPsMat, y_imit]=movementImitation(target, contexts);

contexts.refTraj = y_predProMPsMat';
contexts.refVar = y_predVar';

num_obst = 1;
weights = [zeros(1,7)+30,30, zeros(1,5*num_obst), 0, 0]';

T=200;
M = 10;
err_dist = zeros(1,M+1);

jointsTraj(contexts, weights, config);
plotTrajectory(y_imit,y_adapt,contexts,y_improved);

d = 0;
for t = 1:T
    d = d + sum((joints_improved(t,:)-joints_adapt(t,:)).^2);
end
err_dist(1) = d/T;
for k = 1:M
    contexts.iteration = k;
   
    weights_improved = learningWeights(joints_adapt, joints_improved, contexts, weights, config);
    fprintf('Finished Learning Iteration: %d\n', k);
    weights = weights_improved
    
    jointsTraj(contexts, weights, config);
    plotTrajectory(y_imit,y_adapt,contexts,y_improved);

    
    %% Learning error compared to improved trajectory
    d = 0;
    for t = 1:T
        d = d + sum((joints_improved(t,1:3)-joints_adapt(t,1:3)).^2);
    end
    err_dist(k+1) = d/T;
end

figure;
lw = 2;
fs = 26;
plot([0:10],err_dist,'MarkerFaceColor','black','Marker','s','linewidth',lw*2);
grid on;
xlabel('Learning iterations', 'fontsize', fs);
ylabel('Learning error (rad^2                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   